  				'900': 'var(--color-primary-900)',
  				DEFAULT: 'hsl(var(--primary))',
  				foreground: 'hsl(var(--primary-foreground))'
  			},
  			secondary: {
  				'50': 'var(--color-secondary-50)',
  				'100': 'var(--color-secondary-100)',
  				'200': 'var(--color-secondary-200)',
  				'300': 'var(--color-secondary-300)',
